        mImageUrls.add("data:image/jpeg;base64,");
        mNames.add("Mango");

        mImageUrls.add("data:image/jpeg;base64,");
        mNames.add("Pineapple");


        mImageUrls.add("data:image/jpeg;base64,");
        mNames.add("Orange");

